mapper.select:
SELECT request_id AS requestId, attendance_id AS attendanceId, before_attendance_code_id AS beforeAttendanceCodeId, requested_attendance_code_id AS requestedAttendanceCodeId, request_reason AS requestReason, status AS status, requested_by AS requestedBy, requested_at AS requestedAt, decided_by AS decidedBy, decided_at AS decidedAt, admin_comment AS adminComment, pending_flag AS pendingFlag FROM tbl_attendance_correction_request
attendance_id = #{attendanceId}
AND requested_by = #{requestedBy}
AND status = #{status}
AND requested_at >= #{fromDateTime}
AND requested_at <= #{toDateTime}
ORDER BY requested_at DESC, request_id DESC
LIMIT #{limit} OFFSET #{offset}

SELECT COUNT(*)
FROM tbl_attendance_correction_request


        attendance_id = #{attendanceId}


        AND requested_by = #{requestedBy}


        AND status = #{status}


        AND requested_at  >=  #{fromDateTime}


        AND requested_at  <=  #{toDateTime}
